mapper.select:
SELECT * FROM ( SELECT '입고' AS storestatus , i.ITEMNAME itemname , s.STOREPRICE storeprice , s.STORECOUNT storecount , s.STOREDATE storedate , s.salecount salecount , i.ITEMNUM itemnum FROM STORETABLE s INNER JOIN ITEMTABLE i ON s.ITEMNUM = i.ITEMNUM UNION SELECT '출고' AS storestatus , i.ITEMNAME itemname , o.ORDERDETAILPRICE storeprice , o.ORDERDETAILCOUNT storecount , o.ORDERDETAILSALEDATE storedate , s.salecount salecount , i.ITEMNUM itemnum FROM ORDERDETAILTABLE o INNER JOIN STORETABLE s ON o.STORENUM =s.STORENUM INNER JOIN ITEMTABLE i ON s.ITEMNUM =i.ITEMNUM WHERE ORDERDETAILSALEDATE IS NOT NULL
) Store
AND Store.storestatus = #{storeStatus}
AND Store.itemnum = #{itemNum}
ORDER BY Store.storeDate ASC
